package advent.year2023

fun main() {
    part1_and_2("part 1",2L)
    part1_and_2("part 2",1000000L)
}

fun part1_and_2(part : String, multiple : Long) {

    fun diff(galaxy1: Pair<Long, Long>, galaxy2: Pair<Long, Long>) =
        (if (galaxy2.first - galaxy1.first > 0) galaxy2.first - galaxy1.first else galaxy1.first - galaxy2.first) +
            (if (galaxy2.second - galaxy1.second > 0) galaxy2.second - galaxy1.second else galaxy1.second - galaxy2.second)

    val dayArray = day11map.split("\n").toTypedArray()
    val emptyRows = dayArray.mapIndexedNotNull { index, s ->
        if (!s.contains("#")) index else null
    }

    val emptyCols = (0 until dayArray[0].length).mapNotNull { colIndex ->
        if ((0 until dayArray.size).filter { rowIndex ->
                dayArray[rowIndex][colIndex] == '#'
            }.isEmpty()) colIndex else null
    }

    val galaxyGrid = dayArray.mapIndexed { ir, row ->
        row.mapIndexedNotNull { ic, col ->
            if (col == '#') {
                val emptyRCount = emptyRows.filter { ir > it }.size.toLong()
                val emptyCCount = emptyCols.filter { ic > it }.size.toLong()

                Pair((ir + if ( emptyRCount>0) emptyRCount*multiple-emptyRCount else 0),
                    (ic+ if (emptyCCount >0) emptyCCount*multiple-emptyCCount else 0))
            } else null
        }
    }.flatten().let { list ->
        list.windowed(list.size, partialWindows = true).filter { it.size >= 2 }.map { pair ->
            pair.drop(1).map { diff(it, pair.first()) }.reduce { acc, s ->
                acc + s
            }
        }.sum()
    }

    println("answer to $part is $galaxyGrid")
}

